import React from 'react';
import './Components Styles/login.css'
import { useNavigate } from 'react-router-dom';
import { CartState } from '../App';

const SignIn = () => {
    const navigate = useNavigate();
    const { setLogged } = CartState();


    const log = (e) => {
        var flag = true;
        e.preventDefault();
        const input = document.querySelectorAll("#registerForm input");
        input.forEach((val) => {
            if (val.type === "email") {
                var validRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
                if (validRegex.test(val.value) === false) {
                    document.querySelector("#notice").innerHTML = "Please enter a valid email."
                    flag = false;
                }
            }
            if (val.type === "text" || val.type === 'password') {
                if (val.value === "") {
                    document.querySelector("#notice").innerHTML = "Fill all fields."
                    flag = false;
                } else if (val.value.length < 3) {
                    document.querySelector("#notice").innerHTML = "fields length greater than 2."
                    flag = false;
                }
            }
            if (flag) {
                document.querySelector("#notice").innerHTML = " Your Data is Saved.";
                document.querySelector("#notice").style = "color:green;"
                setLogged(true);
        navigate('/menu')
            }

        })

    }


    return (
        <div id='registerContainer'>
            <h1 style={{ textAlign: "center", fontSize: "3vw", paddingTop: "5vh" }}>Register</h1>
            <form id='registerForm'>
                <div>
                    <h1>Email-Id</h1>
                    <input type='email' placeholder='email@abc.com' require />
                    <h1>User Name </h1>
                    <input type='text' placeholder='username' require />
                    <h1> Password </h1>
                    <input type='password' placeholder='password' require />
                    <h1> Re-Enter Password </h1>
                    <input type='text' placeholder='password' require />
                    <p id="notice"></p>
                    <button onClick={log}> Register</button>
                </div>
            </form>
        </div>
    )
}

export default SignIn
