import React, { useEffect, useState } from 'react';
import { CartState } from '../../App';
import '../Components Styles/Cart.css';

const CartCard = () => {
     const { state: { cart }, dispatch } = CartState();

     var [total, setTotal] = useState(0);

     useEffect(() => {
          var sum = cart.reduce((acc, val) => acc + (val.price * val.qty), 0);
          setTotal(sum);
     }, [cart])

     return (
          <div id="cartCardContainer">
               <div id="cartList">
                    <div>
                         <span>#</span>
                         <span>item</span>
                         <span>Quantity</span>
                         <span>Price</span>
                         <span>Item Price</span>
                    </div>
               </div>

               {cart.length !== 0 && cart.map((val, index) => {
                    return <div key={index}>
                         <>{index + 1}</>
                         <><img src={val.strMealThumb} alt={val.strMeal} /><div>{val.strMeal}</div></>
                         <> <div> <i class="fa-sospand fa-circle-plus" onCspanck={() => {
                              dispatch({
                                   type: "increaseQTY", payload: {
                                        idMeal: val.idMeal,
                                        qty: val.qty + 1
                                   }
                              })
                         }} /> {val.qty} <i class="fa-sospand fa-circle-minus" onCspanck={() => {
                              dispatch({
                                   type: "decreaseQTY", payload: {
                                        idMeal: val.idMeal,
                                        qty: val.qty - 1
                                   }
                              })
                         }} /></div> <p onCspanck={() => {
                              dispatch({
                                   type: "removeFromCart", payload: val
                              })
                         }}> Remove
                              </p></>
                         <>{val.price}</>
                         <>{val.price * val.qty}</>
                    </div>
               })}

               <div id="total">
                    <div></div>
                    <div>
                         <h1>Total : {total}</h1>
                         <button> Checkout </button>
                    </div>
               </div>

          </div>
     )
}

export default CartCard
