import { createContext, useContext, useReducer, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import AboutPage from './Components/AboutPage';
import Cart from './Components/Cart';
import Checkout from './Components/Checkout';
import Footer from './Components/Footer';
import Main from './Components/Main';
import Menu from './Components/Menu';
import NavBar from './Components/NavBar';

import sweet from './Assests/Dessert.json';
import { cartReducer, productReducer } from './Components/Reducer';
import { Alert, Box, Button, Dialog, DialogActions, DialogTitle, Fade, Modal, Snackbar, Typography } from '@mui/material';
import Login from './Components/Login';
import SignIn from './Components/SignIn';


const user = createContext();

function App() {

  // Dialog Box state 
  const [open, setOpen] = useState({ open: false, html: "", type: "", value: null });
  // Snackbar State
  const [openSnack, setOpenSnack] = useState({ open: false, html: "", severity: "success", time: "800" });
  // State for login
  const [logged, setLogged] = useState(false);
  const [userName, setUserName] = useState("");
  // state for counting total Value
  const [total, setTotal] = useState(0);
  // State for Modal
  const [openModal, setOpenModal] = useState({ val: [], open: false })

  const [state, dispatch] = useReducer(cartReducer, {
    products: sweet,
    cart: []
  });

  const [productState, productDispatch] = useReducer(productReducer, {
    search: "",
    sortPrice: false,
    sortName: false
  })

  const confirm = () => {
    if (open.type === 'trash') {
      dispatch({
        type: "removeFromCart", payload: open.value
      })
      setOpen({ ...open, open: false });
      setOpenSnack({ open: true, html: `${open.value.strMeal} removed from cart !` })
    }
    if (open.type === 'empty') {
      dispatch({
        type: "clearCart"
      })
      setOpen({ ...open, open: false });
      setOpenSnack({ open: true, html: ` Your cart is emptied !!` })
    }
  }

  return (
    <>
      <user.Provider value={{ state, dispatch, productState, productDispatch, open, setOpen, openSnack, setOpenSnack, logged, setLogged, userName, setUserName, total, setTotal, openModal, setOpenModal }} >

        <div id='body' style={{ backgroundColor: "var(--bg)" }}>
          <Router >
            <NavBar />
            <Routes>
              <Route path='/' exact element={<Main />} />
              <Route path='/about' element={<AboutPage />} />
              <Route path='/cart' element={<Cart />} />
              <Route path='/menu' element={<Menu />} />
              <Route path='/checkout' element={<Checkout />} />
              <Route path='/login' element={<Login />} />
              <Route path='/create' element={<SignIn />} />
            </Routes>
            <Footer />
          </Router>


          {/* Dialog component from MUI */}
          <Dialog open={open.open} onClose={() => { setOpen({ ...open, open: false }) }}>
            <DialogTitle > {open.html} </DialogTitle>
            <DialogActions>
              <Button autoFocus onClick={() => { setOpen({ ...open, open: false }) }} >
                Cancel
              </Button>
              <Button onClick={confirm}>Ok</Button>
            </DialogActions>
          </Dialog>


          {/* Snackbar Component from MUI */}

          <Snackbar open={openSnack.open} autoHideDuration={Number(openSnack.time)} onClose={() => { setOpenSnack({ ...open, open: false }) }} anchorOrigin={{ vertical: "top", horizontal: 'center' }} >
            <Alert onClose={() => { setOpenSnack({ ...openSnack, open: false }) }} severity={openSnack.severity} variant='filled' sx={{ width: '100%' }}>
              {openSnack.html}
            </Alert>
          </Snackbar>


          {/* Modal for Products details */}

          <Modal closeAfterTransition open={openModal.open} onClose={() => setOpenModal({ ...openModal, open: false })}>
            <Fade in={openModal.open}>
              <Box sx={style}>
                <i class="fa-solid fa-xmark"/>
                <Typography variant='h3' sx={{ fontSize: "1.5rem", textAlign: "center" }}> {openModal.val.strMeal}</Typography>
              </Box>
            </Fade>
          </Modal>

        </div>
      </user.Provider>
    </>
  );
}

export default App;

export const CartState = () => {
  return useContext(user)
}

// Modal style

const style = {
  position: 'absolute',
  top: '15%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '70%',
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};
