import { Typography } from '@mui/material';
import { Box } from '@mui/system';
import React from 'react'
import { useParams } from 'react-router-dom'
import { CartState } from '../App'

const Product = () => {
    const params = useParams();
    const id = params.ID;
    const { state, dispatch, setOpenSnack } = CartState();
    let q
    return (
        <>
            <Box >
                <div id='detail'>
                    <div>
                        <img src={val.strMealThumb} alt={val.strMeal} style={{ width: "300px" }} />
                    </div>
                    <div style={{ flex: "2", display: "flex", flexDirection: "column", justifyContent: "space-evenly" }}>
                        <Typography sx={{ fontSize: "2rem", textAlign: "start" }}> {val.strMeal}</Typography>
                        <Typography sx={{ fontSize: "1.5rem", textAlign: "start" }}> Price: â‚¹{val.price} <span className='wrongPrice'>{val.price + 50}</span> </Typography>
                        <Typography>
                            <div className='quantity'>
                                Quantity: {state.cart.some((p) => p.idMeal === val.idMeal) ? <> <i className="fa-solid fa-circle-plus" onClick={() => {
                                    dispatch({
                                        type: "increaseQTY", payload: {
                                            idMeal: val.idMeal,
                                            qty: quan + 1
                                        }
                                    })
                                }} />
                                    {state.cart.forEach((p) => { if (p.idMeal === val.idMeal) quan = p.qty })}
                                    {quan !== null && quan}
                                    <i className="fa-solid fa-circle-minus" onClick={() => {
                                        dispatch({
                                            type: "decreaseQTY", payload: {
                                                idMeal: val.idMeal,
                                                qty: quan - 1
                                            }
                                        })
                                    }} />
                                </> : 0}
                            </div>
                        </Typography>
                        <Typography variant='h6'> Area: {val.strArea} </Typography>
                        <Typography variant='h6'> Tags: {val.strTags} </Typography>
                        {quan === 0 ? <button className='addtoCart' onClick={() => {
                            dispatch({
                                type: "addToCart", payload: val
                            })
                            setOpenSnack({ open: true, html: `${val.strMeal} is added to cart`, severity: "success", time: "800" })
                        }} > Add to Cart</button> : <button className='remove addtoCart' onClick={() => {
                            dispatch({
                                type: "removeFromCart", payload: val
                            })
                            setOpenSnack({ open: true, html: `${val.strMeal} is removed from cart`, severity: "success", time: "800" })
                        }}> Remove from Cart</button>}
                        {val.strSource && <Typography element='div'><a href={val.strSource} target='blank' className="link"> Know More</a> </Typography>}
                    </div>
                </div>
            </Box>
            <Typography variant='p' style={{ lineHeight: "1rem" }}> {val.strInstructions}</Typography>
        </>
    )
}

export default Product
