import React, { useEffect, useState } from 'react';
import { CartState } from '../../App';
import '../Components Styles/Cart.css';
import Tippy from '@tippyjs/react'
import 'tippy.js/dist/tippy.css';
import { Button, Dialog, DialogActions, DialogContentText, DialogTitle } from '@mui/material';

const CartCard = () => {
     // Importing state from context
     const { state: { cart }, dispatch } = CartState();
     // state for counting total Value
     var [total, setTotal] = useState(0);
     // Snack Bar state 
     const [open, setOpen] = React.useState({ open: false, html: "", type: "", value: null });

     useEffect(() => {
          var sum = cart.reduce((acc, val) => acc + (val.price * val.qty), 0);
          setTotal(sum);
     }, [cart])

     const confirm = () => {

          if (open.type === 'trash') {
               dispatch({
                    type: "removeFromCart", payload: open.value
               })
          }


          // () => {
          //      dispatch({
          //           type: "removeFromCart", payload: val
          //      })
          // }
     }

     return (
          <div id="cartCardContainer">
               <div id="cartList">
                    {cart.length !== 0 && cart.map((val, index) => {
                         return <div key={index} className="cartProductContainer">
                              <div className='cartProductDetails'>
                                   <img src={val.strMealThumb} alt={val.strMeal} />
                                   <h3> {val.strMeal}</h3>
                              </div>
                              <div className='cartProductPrice'>
                                   <p> Price: ₹ {val.price}</p>
                                   <p>Item Price: ₹ {val.price * val.qty}</p>
                              </div>
                              <div className="cartProductQuantity">
                                   <div className='quantity'>
                                        <i className="fa-solid fa-circle-plus" onClick={() => {
                                             dispatch({
                                                  type: "increaseQTY", payload: {
                                                       idMeal: val.idMeal,
                                                       qty: val.qty + 1
                                                  }
                                             })
                                        }} /> {val.qty} <i className="fa-solid fa-circle-minus" onClick={() => {
                                             dispatch({
                                                  type: "decreaseQTY", payload: {
                                                       idMeal: val.idMeal,
                                                       qty: val.qty - 1
                                                  }
                                             })
                                        }} />
                                   </div>
                                   <div className='deleteItem'>
                                        <Tippy content='delete from cart' placement="left">
                                             <i class="fa-solid fa-trash-can" onClick={setOpen({ ...open, open: true, html: "Are your to delete this product from cart ?", type: "trash", value: val })}></i>
                                        </Tippy>
                                   </div>
                              </div>
                         </div>



                    })}
               </div>

               <div id="total">
                    <div> <button className='clear' onClick={() => {
                         dispatch({
                              type: "clearCart"
                         })
                    }}> Empty Cart </button></div>
                    <div>
                         <h1>Total: ₹{total}</h1>
                         <button className='checkout'> Checkout </button>
                    </div>
               </div>


                {/* Dialog component from MUI */}
                <Dialog open={open.open} onClose={() => { setOpen({ ...open, open: false }) }}>
                    <DialogTitle > {open.html} </DialogTitle>
                    <DialogActions>
                         <Button autoFocus onClick={() => { setOpen({ ...open, open: false }) }} >
                              Cancel
                         </Button>
                         <Button onClick={confirm}>Ok</Button>
                    </DialogActions>
               </Dialog>

          </div>
     )
}

export default CartCard
