import React from 'react';
import './Components Styles/login.css'
import { useNavigate } from 'react-router-dom';
import { CartState } from '../App';
import data from '../Assests/userData.json'

const SignIn = () => {
    const navigate = useNavigate();
    const { setLogged } = CartState();

    const log = (e) => {
        var flag = true;
        e.preventDefault();
        const emailLog = document.querySelector("#emailSign").value.toLowerCase();
        const passlogin = document.querySelector("#signPass").value;
        const resignPass = document.querySelector("#resignPass").value;
        const uname = document.querySelector('#signUname').value;
        var validRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;

        if (validRegex.test(emailLog) === false) {
            document.querySelector("#notice").innerHTML = "*Please enter a valid email."
            document.querySelector("#notice").style = "color:red;font-size:1.3rem;"
            flag = false;
        }
        if (uname === "") {
            document.querySelector("#notice").innerHTML = "*Fill username."
            document.querySelector("#notice").style = "color:red;font-size:1.3rem;"
            flag = false;
        } else if (uname < 3) {
            document.querySelector("#notice").innerHTML = "*username length greater than 3."
            document.querySelector("#notice").style = "color:red;font-size:1.3rem;"
            flag = false;
        }

        if (passlogin === "") {
            document.querySelector("#notice").innerHTML = "*Password can't be empty."
            document.querySelector("#notice").style = "color:red;font-size:1.3rem;"
            flag = false;
        } else if (passlogin < 3) {
            document.querySelector("#notice").innerHTML = "*Password should be length greater than 3."
            document.querySelector("#notice").style = "color:red;font-size:1.3rem;"
            flag = false;
        }


        if (flag) {
            document.querySelector("#emailSign").value = '';
            document.querySelector("#signPass").value = '';
            document.querySelector("#resignPass").value = '';
            document.querySelector('#signUname').value = '';
            document.querySelector("#notice").innerHTML = "";
            setLogged(`${uname}`);

            navigate('/menu')
        }

    }


    return (
        <div id='registerContainer'>
            <h1 style={{ textAlign: "center", fontSize: "3vw", paddingTop: "5vh" }}>Register</h1>
            <form id='registerForm'>
                <div>
                    <h1>Email-Id</h1>
                    <input type='email' placeholder='email@abc.com' require id='emailSign' />
                    <h1>User Name </h1>
                    <input type='text' placeholder='username' require id='signUname' />
                    <h1> Password </h1>
                    <input type='password' placeholder='password' require id='signPass' />
                    <h1> Re-Enter Password </h1>
                    <input type='text' placeholder='password' require id='resignPass' />
                    <p id="notice"></p>
                    <button onClick={log}> Register</button>
                </div>
            </form>
        </div>
    )
}

export default SignIn
