import React from 'react'
import { Link } from 'react-router-dom';
import '../App.css'

const NavBar = () => {


    const menu = () => {
        document.querySelector('.search-form').classList.remove('active');
        document.querySelector(".navbarActive").classList.toggle("active");
    }
    const login = (e) => {
        e.preventDefault();
        document.querySelector(".loginContainer").classList.toggle("active");
        document.querySelector(".search-form").classList.remove("active");
        document.querySelector(".navbarActive").classList.remove("active");
        document.querySelector(".signContainer").classList.remove("active");
        document.querySelector("#detailContainer").style = "display:none";
    }
    const sign = (e) => {
        e.preventDefault();
        document.querySelector(".signContainer").classList.toggle("active");
        document.querySelector(".search-form").classList.remove("active");
        document.querySelector(".navbarActive").classList.remove("active");
        document.querySelector(".loginContainer").classList.remove("active");
        document.querySelector("#detailContainer").style = "display:none";
    }


    return (
        <header className="header">
            <h1 className="logo">
                <Link to="/"><img src={'https://cdn1.storehippo.com/s/573db1519f0d58741f0cc5c1/ms.settings/5256837ccc4abf1d39000001/webp/589eeda381c872391b08f498-480x480.png'} alt="logo" /></Link>
            </h1>
            {/* For Navbar on Desktop */}
            <nav className="navbar">
                <h1><Link to="/" style={{ color: "var(--black)" }}>Home</Link></h1>
                <h1><Link to="/" style={{ color: "var(--black)" }}>Menu</Link></h1>
                <h1><Link to="/" style={{ color: "var(--black)" }}>Review</Link></h1>
                <h1><Link to="/cart"><i className="fas fa-shopping-cart cart-btn" > <span></span></i></Link></h1>
            </nav>

            {/* For NavBar on mobile screen */}
            <nav className="navbarActive">
                <h1><Link to="/" style={{ color: "var(--black)" }}>Home</Link></h1>
                <h1><Link to="/" style={{ color: "var(--black)" }}>Menu</Link></h1>
                <h1><Link to="/" style={{ color: "var(--black)" }}>Review</Link></h1>
                <h1><Link to="/cart"><i className="fas fa-shopping-cart cart-btn" > <span></span></i></Link></h1>
            </nav>
        </header>
    )
}

export default NavBar
